mapper.select:

select distinct a.*, ( select count(*) from boardcomment where bno =a.bno ) as count
from ( select rownum as r, bno, boriginalfilename, btitle, bwriter,	bdate, bhitcount, blikecount
	from ( select  bno, boriginalfilename, btitle, bwriter, bdate, bhitcount, blikecount 
		from board order by bno desc
	) 
	where rownum <=#{endNum}
) a, 
boardcomment b  
where r >=#{startNum} and a.bno = b.bno(+)
order by a.bno desc
