mapper.select:

select distinct a.*, ( select count(*) from boardcomment where bno =a.bno ) as count
from ( select bno, boriginalfilename, btitle, bwriter,	bdate, bhitcount, blikecount
	from ( select  bno, boriginalfilename, btitle, bwriter, bdate, bhitcount, blikecount 
		from board order by bno desc
	) as c 			
) a left join  
boardcomment b  
on  a.bno = b.bno
order by a.bno desc
limit #{startNum}, #{endNum}
